/**
 * Unit Tests for the class
**/

#include <gtest/gtest.h>
#include "Guesser.h"

class GuesserTest : public ::testing::Test
{
	protected:
		GuesserTest(){} //constructor runs before each test
		virtual ~GuesserTest(){} //destructor cleans up after tests
		virtual void SetUp(){} //sets up before each test (after constructor)
		virtual void TearDown(){} //clean up after each test, (before destructor)
};

// Example "smoke test" (can be deleted)
TEST(GuesserTest, happy_test)
{
  Guesser object("Secret");
  ASSERT_EQ(object.match("Semcrat"),false);
  ASSERT_EQ(object.remaining(),3);
  ASSERT_EQ(object.match("Secret"),false);
}
TEST(GuesserTest, happy2_test)
{
  Guesser object("Secret");
  ASSERT_EQ(object.match("Smcrat"),false);
  ASSERT_EQ(object.remaining(),2);
  ASSERT_EQ(object.match("Secret"),true);
}
TEST(GuesserTest, empty_test)
{
  Guesser object("");
  ASSERT_EQ(object.match(""),true);
}
TEST(GuesserTest, one_test)
{
  Guesser object("a");
  ASSERT_EQ(object.match(""),false);
  ASSERT_EQ(object.remaining(),2);
  ASSERT_EQ(object.match("a"),true);
}
TEST(GuesserTest, to_far_test)
{
  Guesser object("hello");
  ASSERT_EQ(object.match(""),false);
  ASSERT_EQ(object.match("hello"),false);
  ASSERT_EQ(object.match("hello"),false);
  ASSERT_EQ(object.match("hello"),false);
  ASSERT_EQ(object.match("hello"),false);
  ASSERT_EQ(object.match("hello"),false);
  ASSERT_EQ(object.match("hello"),false);
  ASSERT_EQ(object.match("hello"),false);
  ASSERT_EQ(object.match("hello"),false);
  ASSERT_EQ(object.match("hello"),false);
  ASSERT_EQ(object.match("hello"),false);
  ASSERT_EQ(object.match("hello"),false);
  ASSERT_EQ(object.match("hello"),false);
  ASSERT_EQ(object.match("hello"),false);
  ASSERT_EQ(object.match("hello"),false);
  ASSERT_EQ(object.match("hello"),false);
  ASSERT_EQ(object.match("hello"),false);
}
TEST(GuesserTest, too_many_test)
{
  Guesser object("Secret");
  ASSERT_EQ(object.match("Secre"),false);
  ASSERT_EQ(object.remaining(),2);
  ASSERT_EQ(object.match("Secre"),false);
  ASSERT_EQ(object.remaining(),1);
  ASSERT_EQ(object.match("Secre"),false);
  ASSERT_EQ(object.remaining(),0);
  ASSERT_EQ(object.match("Secre"),false);
  ASSERT_EQ(object.remaining(),0);
  ASSERT_EQ(object.match("Secret"),false);
}
TEST(GuesserTest, line_test)
{
  Guesser object("Secret");
  ASSERT_EQ(object.match("hell0\nmy\nname\nis\ncooper"),false);
  
}
TEST(GuesserTest, object_test)
{
  // this should reduce the size 
  Guesser object("abcdabcdabcdabcdabcdabcdabcdabcd");
  ASSERT_EQ(object.match("abcdabcdabcdabcdabcdabcdabcdabc"),true);
  
}
TEST(GuesserTest, long_test)
{
  Guesser object("Secret");
  ASSERT_EQ(object.match(""),false);
  
}

